import React, { Component as C } from "react";
export class Counter extends C {
	constructor(props) {
		super();
		const { stars } = props;
		this.state = { stars };
	}

	componentWillReceiveProps(props) {
		if (this.props.stars !== props.stars) {
			console.log(parseInt(props.stars));
			this.setState({
				stars: parseInt(props.stars)
			});
		}
	}

	plus() {
		const stars = +this.state.stars + 1;
		this.setState({ stars });
	}
	render() {
		return (
			<div>
				Лайкер <hr />
				<button onClick={() => this.plus()}>
					<span role="img" aria-label="add">
						➕
					</span>
				</button>{" "}
				<Stars length={this.state.stars} />
			</div>
		);
	}
}

const Stars = ({ length }) => (
	<span>
		{Array.from({ length }, (v, i) => ++i)
			.map(() => "⭐️")
			.join("")}
	</span>
);
